$(document).ready(function () {
    $(".sidenav").sidenav()
}), $(".dropdown-trigger").dropdown({coverTrigger: !1}), $(document).ready(function () {
    $(".materialboxed").materialbox();
    $('.modal').modal();
    $('.ajax-btn').click(function(){
            var user_name    = $('[name="name"]').val();
            var user_email   = $('[name="email"]').val();
            var user_surname = $('[name="surname"]').val();
            var subject = $('[name="subject"]').val();
            var text_comment = $('[name="message"]').val();
            $.ajax({
                url: "/send-email",
                type: "get",
                data: {
                    "name":    user_name,
                    // "surname": user_surname,
                    "email":   user_email,
                    "subject":   subject,
                    "message": text_comment
                },
                error:function(){$("#erconts").html("Произошла ошибка!");},
                beforeSend: function() {
                    $("#erconts").html("Отправляем данные...");
                },
                success: function(result) {
                    $('#erconts').html(result);
                    console.log("ntcn");
                }
            });
    });

});